package simsgame;

/**
 * @author Hugh
 *
 */
public class Person {

	private String name;
	
	/**
	 * default constructor
	 */
	public Person() {
		// TODO Auto-generated constructor stub
	}

	/**
	 * constructor with args
	 * 
	 * @param name
	 */
	public Person(String name) {
		this.name = name;
	}

	/**
	 * @return the name
	 */
	public String getName() {
		return name;
	}

	/**
	 * @param name the name to set
	 */
	public void setName(String name) {
		this.name = name;
	}
		

}
